package Entity;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Id;import javax.persistence.Table;import java.io.Serializable;@Entity(name = "Person_Entity")@Table(name = "person_entity")public class PersonEntity implements Serializable {    private Long id;    private static final long serialVersionUID = 3247358156921423429L;    @Id    @Column(name = "id", nullable = false)    public Long getId() {        return id;    }    public PersonEntity setId(Long id) {        this.id = id;        return this;    }}