package com.pokerface.spingboot;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Id;import javax.persistence.Table;import java.io.Serializable;@Entity(name = "Post")@Table(name = "post")public class Post implements Serializable {    private static final long serialVersionUID = 8980484085196712943L;    private Long id;    private String anons;    private String fullText;    private String title;    private Integer views;    @Id    @Column(name = "id", nullable = false)    public Long getId() {        return id;    }    public Post setId(Long id) {        this.id = id;        return this;    }    @Column(name = "anons")    public String getAnons() {        return anons;    }    public Post setAnons(String anons) {        this.anons = anons;        return this;    }    @Column(name = "full_text")    public String getFullText() {        return fullText;    }    public Post setFullText(String fullText) {        this.fullText = fullText;        return this;    }    @Column(name = "title")    public String getTitle() {        return title;    }    public Post setTitle(String title) {        this.title = title;        return this;    }    @Column(name = "views", nullable = false)    public Integer getViews() {        return views;    }    public Post setViews(Integer views) {        this.views = views;        return this;    }}