package com.pokerface.spingboot;import java.io.Serializable;import java.util.Objects;public class PostDto implements Serializable {    private final Long id;    private final String anons;    private final String fullText;    private final String title;    private final Integer views;    public PostDto(Long id, String anons, String fullText, String title, Integer views) {        this.id = id;        this.anons = anons;        this.fullText = fullText;        this.title = title;        this.views = views;    }    public Long getId() {        return id;    }    public String getAnons() {        return anons;    }    public String getFullText() {        return fullText;    }    public String getTitle() {        return title;    }    public Integer getViews() {        return views;    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        PostDto entity = (PostDto) o;        return Objects.equals(this.id, entity.id) &&                Objects.equals(this.anons, entity.anons) &&                Objects.equals(this.fullText, entity.fullText) &&                Objects.equals(this.title, entity.title) &&                Objects.equals(this.views, entity.views);    }    @Override    public int hashCode() {        return Objects.hash(id, anons, fullText, title, views);    }    @Override    public String toString() {        return getClass().getSimpleName() + "(" +                "id = " + id + ", " +                "anons = " + anons + ", " +                "fullText = " + fullText + ", " +                "title = " + title + ", " +                "views = " + views + ")";    }}